import 'dart:typed_data';

import 'package:codec_utils/codec_utils.dart';
import 'package:cryptography_utils/cryptography_utils.dart';
import 'package:test/test.dart';

void main() {
  group('Tests of SystemAssignInstruction constructor', () {
    test('Should [return SystemAssignInstruction]', () {
      // Act
      SystemAssignInstruction actualSystemAssignInstruction = const SystemAssignInstruction(
        programIdString: '11111111111111111111111111111111',
        recipientString: 'Recipient11111111111111111111111111111111111111',
        signerString: 'Signer111111111111111111111111111111111111111',
      );

      // Assert
      String expectedProgramId = '11111111111111111111111111111111';
      String expectedRecipient = 'Recipient11111111111111111111111111111111111111';
      String expectedSigner = 'Signer111111111111111111111111111111111111111';

      expect(actualSystemAssignInstruction.programIdString, expectedProgramId);
      expect(actualSystemAssignInstruction.recipientString, expectedRecipient);
      expect(actualSystemAssignInstruction.signerString, expectedSigner);
      expect(actualSystemAssignInstruction.programId, expectedProgramId);
      expect(actualSystemAssignInstruction.recipient, expectedRecipient);
      expect(actualSystemAssignInstruction.signer, expectedSigner);
    });
  });

